var _Assets672916bf30f4b5edaf8ff91ca50d7ab8815109be = "{{define \"fossil-politics\"}}\n{{template \"base_header\" .title}}\n<section class=\"fossil-banner jumbotron-fluid d-flex align-items-center\">\n    <div class=\"container\">\n        <div class=\"fossil-banner-content col-lg-6\">\n            <h1>Fossil Politics</h1>\n            <p class=\"text-justify\">This track investigates our massive dependence on fossil fuels and builds from the\n                power of the communities who subsidise this perverse industry and who suffer gross despoliation. We\n                believe that a transition from fossil fuels must be rapid and urgent and devoid of distractions by way\n                of fixes that depend on the status quo.</p>\n        </div>\n    </div>\n</section>\n<section class=\"latest-news container-fluid\">\n    <div class=\"container\">\n        <h5>Fossil Politics - News</h5>\n        <div class=\"row post-top\">\n            <div class=\"card-deck\">\n            {{ range .posts}}\n                    {{template \"card-item\" .}}\n                {{end}}\n            </div>\n        </div>\n    </div>\n</section>\n<section class=\"featured container-fluid\">\n    <div class=\"container\">\n        <div class=\"row d-flex align-items-center\">\n            <div class=\"col-lg-6\"><img class=\"img-fluid\" src=\"/public/wp-content/uploads/2018/08/Living-with-Fear.jpg\" alt=\"\"></div>\n            <div class=\"col-lg-6\">\n                <h2>Living in Fear</h2>\n                <h5>Juan Lopez Villa</h5>\n                <p>This book explores the general relation between wars, conflicts and natural resources, focusing in particular on two African countries: Democratic Republic of Congo (DRC) and South Sudan.</p>\n                <button type=\"button\" class=\"btn btn-outline-secondary\">Download</button>\n            </div>\n        </div>\n    </div>\n\n</section>\n\n{{template \"base_footer\"}}\n{{end}}"
var _Assetsf613e6f9e9065a175028a525b4fc17cc9a3d5009 = "{{define \"about-us\"}}\n{{template \"base_header\"}}\n<section class=\"aboutus-banner jumbotron-fluid d-flex align-items-center\">\n    <div class=\"container\">\n        <div class=\"aboutus-banner-content col-lg-6\">\n            <h1>About HOMEF</h1>\n            <p>We are an ecological think tank organization advocating for environmental / climate justice and food\n                sovereignty in Nigeria and Africa at large.</p>\n        </div>\n    </div>\n</section>\n<section class=\"container homef-reason\">\n    <h1 class=\"text-center mt-5\">Why HOMEF?</h1>\n    <div class=\"row\">\n        <blockqoute class=\"blockquote text-center\">\n            <p>Exploitation of Nature is a reflection of the unjust relations between people and the social, political,\n                gender, economic crises in society. Dissatisfied with the impunity in our environment, we believe\n                that:</p>\n        </blockqoute>\n    </div>\n    <div class=\"row three-reasons\">\n        <div class=\"col-lg-4\">\n            <img class=\"mx-auto\" src=\"/public/wp-content/uploads/2018/08/Rights.png\" alt=\"\">\n            <p>The rights of Mother Earth must be safe guarded</p>\n\n        </div>\n        <div class=\"col-lg-4\"><img src=\"/public/wp-content/uploads/2018/08/Community.png\" alt=\"\">\n            <p>Communities should be equipped to voice protest against oppression and pollution in their environment</p>\n        </div>\n        <div class=\"col-lg-4\"><img src=\"/public/wp-content/uploads/2018/08/Justice.png\" alt=\"\">\n            <p>Justice must prevail in our engagement with the environment and natureâs cycles at policy, corporate, and\n                individual level</p></div>\n    </div>\n</section>\n<section class=\"container-fluid homef-approach\">\n    <div class=\"container\">\n        <div class=\"row\">\n            <h1>Our Approach</h1>\n            <p>We believe in locally generated knowledge and practice, and the need for people-centred dialogues.</p>\n            <p>Our thinking is ecological minded, rooted in an ideology of living in harmony with nature and the\n                environment.</p>\n            <p>We are focused on unearthing the systemic roots (social, political and economic factors) of environmental and\n                food challenges.</p>\n        </div>\n    </div>\n</section>\n<section class=\"container project-tracks\">\n    <h1>Our Project Tracks</h1>\n    <div class=\"row\">\n        <div class=\"col-lg-4\"><img src=\"/public/img/fossil-politics.png\" alt=\"\">\n            <h4>Fossil Politics</h4>\n            <ul>\n                <li>Environmental Justice</li>\n                <li>Climate Justice</li>\n                <li>Oil, gas, solid minerals</li>\n            </ul>\n        </div>\n        <div class=\"col-lg-4\"><img src=\"/public/img/sustainability-academy.png\" alt=\"\"><h4>Sustsainability Academy</h4>\n            <ul>\n                <li>Contestation of ideas</li>\n                <li>Generation and sharing of ideas on the components of hunger and fossil politics</li>\n            </ul>\n        </div>\n        <div class=\"col-lg-4\">\n            <img src=\"/public/img/hunger-politics.png\" alt=\"\">\n            <h4>Hunger Politics</h4>\n            <ul>\n                <li>Biosafety</li>\n                <li>Biodiversity (Forests)</li>\n                <li>GMOs, synthetic biology, gene drives</li>\n                <li>Food justice/preserving farmers' rights</li>\n            </ul>\n        </div>\n    </div>\n</section>\n<section class=\"container-fluid advisory-board\">\n    <div class=\"container\">\n        <div class=\"row\">\n            <h4>Advisory Board</h4>\n            <p>The Advisory Board is composed of women and men who have distinguished themselves in the struggle for\n                environmental justice and the rights of Mother Earth:</p>\n            <ul>\n                <li><strong>Chris Allan (USA)</strong> â Environmental health campaigner and philanthropy activist</li>\n                <li><strong>Akinbode Oluwafemi (Nigeria)</strong> â Environmental justice campaigner</li>\n                <li><strong>Siziwe Khanyile (South Africa)</strong> â Environmental justice campaigner</li>\n                <li><strong>George B.K. Awudi (Ghana)</strong> â Climate justice campaigner</li>\n                <li><strong>Evelyn Bassey (Nigeria)</strong> â Environmental justice campaigner</li>\n            </ul>\n            <ul>\n                <li><strong>Esperanza Martinez (Ecuador)</strong> â Environmental justice/Political ecologist</li>\n                <li><strong><a href=\"https://nnimmobassey.net/about/\">Nnimmo Bassey</a> (Nigeria)</strong> â\n                    Environmental\n                    justice advocate\n                </li>\n                <li><strong>Pablo Solon (Bolivia)</strong> â Climate justice campaigner, diplomat and movement builder\n                </li>\n                <li><strong>Liz Hosken (UK)</strong> â Mother Earth rights advocate</li>\n                <li><strong>Lim Li Ching (Malaysia)</strong> â Agro-ecologist and rights advocate</li>\n            </ul>\n        </div>\n    </div>\n</section>\n\n{{ template \"base_footer\"}}\n{{end}}"
